import java.util.Scanner;public class EvenNum {    public static boolean isEven(int num){        return (num % 2) == 0;    }    public static void main(String[] args) {        Scanner input = new Scanner(System.in);        System.out.print("enter the number: ");        int num = input.nextInt();        System.out.println("Number is Even: " + isEven(num));        input.close();    }}